const App = (props) =>{
    const [tasks, setTasks] = useState([]);


const toggleTask = (taskIndex) => {
setTasks(
    tasks.map((task,index) => {
        if(taskIndex === index) {
            return {
                ...task,
                isComplete: !task.isComplete,
            };
        }
        return task;
    })
)}

const addTask = (newTask) => {
    setTasks([ 
        ...tasks,
        {
            name:newTask,
            isComplete:false
        }
    ])
}
    // return (
    //     <Fragment>
    //         <h1>toDo List</h1>
