    //                 </li>
    //                 );
    //             })}
    //         </ul>
    //     </Fragment>
    // )

    return (
        <Fragment>
        <Title title={props.title} />
        <InputField addTask={addTask}/>
        <List items={tasks} toggleTask={toggleTask}/>
        </Fragment>
    )
};

ReactDOM.render(<App title="Title" info="info"/>, document.querySelector("#root"));
